pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

import "./ERC721.sol";
import "./YGYStorageV1.sol";


contract NFT is ERC721, AccessControlUpgradeSafe {
  bytes32 public constant SYSTEM_ROLE = keccak256("SYSTEM_ROLE");
  bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

  uint256 public contractId;

  // Tradeable?
  bool allowTrade;

  // Capped?
  bool isCapped;
  uint256 tokenCap;
  address ramVault;

  // Props for unique token
  mapping(uint256 => YGYStorageV1.NFTProperty) public properties;

  // How many choices available for this particular NFT
  uint256 propertyChoices;

  constructor(
    string memory _name,
    string memory _symbol,
    string memory _tokenURI,
    uint256 _contractId,
    uint256 _propertyChoices,
    address _superAdmin,
    bool _allowTrade,
    bool _isCapped,
    uint256 _tokenCap,
    address _ramVault
  ) public ERC721(_name, _symbol, _tokenURI) {
    allowTrade = _allowTrade;
    isCapped = _isCapped;
    tokenCap = _tokenCap;
    contractId = _contractId;
    propertyChoices = _propertyChoices;
    __AccessControl_init();
    _setupRole(SYSTEM_ROLE, _msgSender());
    _setupRole(SYSTEM_ROLE, _ramVault);
    _setupRole(DEFAULT_ADMIN_ROLE, _superAdmin);
  }

  /**
   * @dev Used for tokens that are transferrable and non-delegating.
   */
  function burn(uint256 _tokenId) external {
    require(ownerOf(_tokenId) == _msgSender() || hasRole(SYSTEM_ROLE, _msgSender()), "Not allowed");

    _burn(_tokenId);
  }

  /**
   * @dev Mint a token and set it's property
   */
  function mint(
    address to,
    uint256 _randomness,
    YGYStorageV1 _storage
  ) public returns (uint256 _tokenId) {
    require(!isCapped || (isCapped && totalSupply() <= tokenCap), "NFT Limit reached");
    require(hasRole(SYSTEM_ROLE, _msgSender()), "Not allowed");

    // Mint the token, get the unique id.
    uint256 tokenId = super.mint(to);
    // Get a random index for property selection
    uint256 propIndex;
    if (propertyChoices > 1) {
      propIndex = _randomness.mod(propertyChoices);
    }
    // Random properties got
    (string memory pType, uint256 pValue, bytes32 extra) = _storage.getNFTProperty(contractId.add(1), propIndex);
    properties[tokenId].pType = pType;
    properties[tokenId].pValue = pValue;
    properties[tokenId].extra = extra;
    return tokenId;
  }

  function getTokenProperty(uint256 _tokenId) public view returns (YGYStorageV1.NFTProperty memory) {
    return properties[_tokenId];
  }

  function setTokenURI(string memory uri) external {
    require(hasRole(SYSTEM_ROLE, _msgSender()), "Bad caller!");
    _tokenURI = uri;
  }

  function transferFrom(
    address from,
    address to,
    uint256 tokenId
  ) public override {
    _beforeTokenTransfer(from, to, tokenId);
    super.transferFrom(from, to, tokenId);
  }

  /**
   * @dev Used to halt transfers while maintaining minting ability for the delegator / minter.
   */
  function _beforeTokenTransfer(
    address from,
    address to,
    uint256 tokenId
  ) internal override {
    require((allowTrade || hasRole(SYSTEM_ROLE, _msgSender())) || (!allowTrade && hasRole(SYSTEM_ROLE, to)), "External trades not allowed");
  }
}
","keccak256":"0xda8a2d12c72853151d51e4ffa704204de68cf2856d21f38816b0a65845f70d78","license":"MIT"},"contracts/NFTFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

import "./interfaces/INFT.sol";
import "./interfaces/IRAMVault.sol";
import "./NFT.sol";

import "@openzeppelin/contracts-ethereum-package/contracts/access/Ownable.sol";
import "./StorageState.sol";

contract NFTFactory is StorageState, OwnableUpgradeSafe {
  address[] public contracts;
  address public bondedContract;
  IRAMVault ramVault;
  // address public lastContractAddress;
  mapping(address => bool) public inUse;

  event NFTMinted(string tokenName, address to, uint256 tokenId);
  event NFTBurned(string tokenName, address from, uint256 tokenId);

  constructor(YGYStorageV1 __storage) public {
    __Ownable_init();
    _storage = __storage;
  }

  function deployNFT(
    string memory name,
    string memory symbol,
    string memory tokenURI,
    uint256 contractId,
    uint256 propertyChoices,
    address admin,
    bool allowTrade,
    bool isCapped,
    uint256 capAmount,
    address _ramVault
  ) public returns (NFT newContract) {
    require(_msgSender() == owner() || _msgSender() == bondedContract, "Invalid caller: can't deploy NFT");
    ramVault = IRAMVault(_ramVault);

    // Deploy new NFT
    NFT nft = new NFT(name, symbol, tokenURI, contractId, propertyChoices, admin, allowTrade, isCapped, capAmount, _ramVault);

    address addressNFT = address(nft);

    // Add to owned NFTs
    contracts.push(addressNFT);

    return nft;
  }

  function balanceOf(INFT _nft, address _who) external view returns (uint256) {
    return _nft.balanceOf(_who);
  }

  function isOwner(
    INFT _nft,
    address _who,
    uint256 _tokenId
  ) external view returns (bool) {
    return _nft.ownerOf(_tokenId) == _who;
  }

  function mint(
    INFT _nft,
    address _to,
    uint256 _randomness
  ) external returns (uint256) {
    require(_msgSender() == bondedContract || _msgSender() == owner());
    uint256 tokenId = _nft.mint(_to, _randomness, _storage);

    emit NFTMinted(_nft.name(), _to, tokenId);
    return tokenId;
  }

  function burn(INFT _nft, uint256 _tokenId) external {
    require(_nft.ownerOf(_tokenId) == _msgSender() || _msgSender() == bondedContract);
    _nft.burn(_tokenId);
    emit NFTBurned(_nft.name(), _msgSender(), _tokenId);
  }

  function setNFTProperties(address _nft, YGYStorageV1.NFTProperty[] memory _properties) external {
    require(msg.sender == owner());
    _storage.setNFTPropertiesForContract(_nft, _properties);
  }

  function useNFT(
    INFT _nft,
    uint256 _tokenId,
    uint256 _poolId
  ) public {
    require(_nft.ownerOf(_tokenId) == msg.sender, "User not owner");
    YGYStorageV1.NFTUsage[] memory nftUsage = _storage.getNFTsInUse(msg.sender);

    uint256 contractId = _nft.contractId();
    bool alreadyInUse;

    // check if nft instance is already in use for the user
    for (uint256 i; i < nftUsage.length; i++) {
      if (nftUsage[i].contractId == contractId) {

        alreadyInUse = true;
        break;
      }
    }
    require(!alreadyInUse, "NFT already in use");

    _nft.transferFrom(msg.sender, address(ramVault), _tokenId);
    ramVault.NFTUsage(msg.sender, address(_nft), _tokenId, _poolId);
  }

  function setNFTUsage(address _nft, bool _inUse) external onlyOwner {
    inUse[_nft] = _inUse;
  }

  function bondContract(address _addr) external returns (bool) {
    require(msg.sender == owner());
    bondedContract = _addr;
    return true;
  }

  function getContractCount() external view returns (uint256 contractCount) {
    return contracts.length;
  }
}
